function Receiver(location) {
    
    var socket = io.connect(location || "http://localhost");
    
    socket.on("telegraph-update", function(data) {

        var name   = data.name
        ,   models = data.models;

        $.cache(name).set("models", models);
        
        if ( $.isArray(models) ) {
            $.each(models, function(i) {
                $.publish(name, i);
            });
        } else {
            $.publish(name, models); 
        }

    });
    
    this.send = socket.emit;
    this.receive = socket.on;

    this.socket = socket;
};
