var vows  = require ("vows"),
       assert = require("assert");

require("../lib/backbone-server");

// ---------------------------------------------------------------------------------------------------- //

vows.describe("Server Properties").addBatch({
    
    "A server" : {

        topic: new Telegraph.Server,
        
        "should have a name property" : function (server) {
            assert.equal("appname" in server, true);
        },

        "should have a database" : function (server) {
            assert.equal("database" in server, true);
        },

        "should include an express property" : function (server) {
            assert.equal("express" in server, true);
        },

        "should include a public directory  property" : function (server) {
            assert.equal("public" in server, true);
        },
        
        "should include a routes property" : function (server) {
            assert.equal("routes" in server, true);
        },

        "should include a socketio property" : function (server) {
            assert.equal("socketio" in server, true);
        },

        "should include a views directory property" : function (server) {
            assert.equal("views" in server, true);
        },
        
         "should include a view engine property" : function (server) {
            assert.equal("view engine" in server, true);
        }
    }                                   
                
}).export(module);