var vows  = require ("vows"),
       assert = require("assert");

require("../lib/backbone-server");

// ---------------------------------------------------------------------------------------------------- //

vows.describe("Setup").addBatch({
    
    "An instance of Backbone Server": {

        topic: Telegraph,

        "should include the server module": function(Backbone) {
            assert.equal(typeof Telegraph.Server, "function");
        },

        "should be able to produce a new server": function(Backbone) {
            var server = new Telegraph.Server();
            assert.equal(typeof server, "object");
        }

    }                    
}).export(module);