var vows  = require ("vows"),
assert  = require("assert");

require("../lib/backbone-server");

// ---------------------------------------------------------------------------------------------------- //

vows.describe("Express Method").addBatch({
    
    "When enabled, the 'socketio' property of  the Backbone Server module": {

        topic: new Telegraph.Server({
            socketio: "true"
        }),
        
        "should exist": function(server) {
            assert.equal("io" in server, true);
        }
        
    }
}).export(module);